const zlib=require("zlib"),fs=require("fs");let raw=fs.readFileSync("./path.json.gzip2"),str=zlib.unzipSync(raw).toString("utf8"),json=JSON.parse(str);for(let key in json){let size=json[key].length;for(let n=0;n<size;n++){let len=json[key][n].length;for(let i=0;i<len;i++)json[key][n][i].x=json[key][n][i].x/1280,json[key][n][i].y=json[key][n][i].y/720}}fs.writeFileSync("./path.json.gzip",zlib.gzipSync(Buffer.from(JSON.stringify(json),"utf8")));